  handleTabClick() {
    this.setState({ active: true });
  }
  render() {
    const tabs = this.state.tabs;
    return (
      <div id="accordion">
        <Tab
          key={tabs.id}
          handleclick={this.handleTabClick}
          id={tabs.id}
          title={tabs.title}
          content={tabs.body}
          active={this.state.active}
        />
      </div>
    );
  }
}
