import React from "react";
import "./style.css";
import "bootstrap/dist/css/bootstrap.css";
import { Link } from "react-router-dom";
import Africa from "./assets/1.png";
import Antarctica from "./assets/2.png";
import Asia from "./assets/3.png";
import Australia from "./assets/4.png";
// import Africa from "./assets/5.png";
// import Africa from "./assets/6.png";
export default class Continents extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      data: [],
    };
  }

  componentDidMount() {
    document.title = "Home page";
    fetch("https://itp404-final-project-backend.herokuapp.com/api/continents")
      .then((response) => {
        return response.json();
      })
      .then((json) => {
        this.setState({
          data: json,
        });
      });
  }
  render() {
    return (
      <div className="container-fluid">
        {this.state.data.map((each) => (
          <div key={each.id}>
            {" "}
            <Link to={`/logs/continents/${each.id}`}>
              <img className="images" src={`${each.name}`} alt={each.name} />
              {each.name}
            </Link>
          </div>
        ))}
        <Link to={`/logs`}>All</Link>
      </div>
    );
  }
}
