import React from "react";
import Tab from "./Tab";
import "./style.css";
export default class Accordion extends React.Component {
  constructor(props) {
    super(props);
    const items = props.tabs;
    items.active = false;
    // for (let i = 0; i < items.length; i++) {
    //   items[i].active = false;
    // }
    this.state = {
      tabs: items,
    };
    this.handleTabClick = this.handleTabClick.bind(this);
    this.updateTabs = this.updateTabs.bind(this);
  }
  updateTabs(id) {
    let tabs = this.state.tabs;
    let newtabs = tabs.map((tab, index) => {
      if (tab.id == id) {
        if (tab.active == true) {
          tab.active = false;
        } else {
          tab.active = true;
        }
      } else {
        tab.active = false;
      }
      return tab;
    });
    return newtabs;
  }
  handleTabClick(id) {
    this.setState({ tabs: this.updateTabs(id) });
  }
  render() {
    const tabs = this.state.tabs;
    console.log(tabs);
    return (
      <div id="accordion">
        <Tab
          key={tabs.id}
          handleclick={this.handleTabClick}
          id={tabs.id}
          title={tabs.title}
          content={tabs.body}
          active={tabs.active}
        />
      </div>
    );
  }
}
