import React from "react";
import { toast } from "react-toastify";
export default class CreateLogForm extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      continent: "",
      country: "",
      year: "",
      title: "",
      body: "",
      continents: [],
    };
  }
  componentDidMount() {
    fetch("http://localhost:4000/api/continents")
      .then((response) => {
        return response.json();
      })
      .then((json) => {
        this.setState({
          continents: json,
        });
        console.log(this.state.continents);
      });
  }
  handleTitleChange(event) {
    this.setState({
      title: event.target.value,
    });
  }
  handleBodyChange(event) {
    this.setState({
      body: event.target.value,
    });
  }
  handleContinentChange(event) {
    this.setState({
      continent: event.target.value,
    });
  }
  handleSubmit(event) {
    event.preventDefault();
    fetch("http://localhost:4000/api/logs", {
      method: "POST",
      body: JSON.stringify({
        title: this.state.title,
        body: this.state.body,
      }),
      headers: {
        "Content-type": "application/json",
      },
    })
      .then((response) => {
        return response.json();
      })
      .then((json) => {
        console.log(json);
        toast.success(`Post: "${json.title}" was successfully created`);
        this.props.history.push("/");
      });
  }
  render() {
    return (
      <form
        onSubmit={(event) => {
          this.handleSubmit(event);
        }}
      >
        <div className="my-3">
          <label
            className="text-sm-right col-form-label col-2"
            htmlFor="continent"
          >
            Continent:
          </label>
          <select
            className="form-select"
            id="continent"
            value={this.state.continent}
            onChange={(event) => {
              this.handleContinentChange(event);
            }}
          >
            <option>--Select Continent--</option>
            {this.state.continents.map((continent) => (
              <option key={continent.id} value={continent.id}>
                {continent.name}
              </option>
            ))}
          </select>
        </div>
        <div className="my-3">
          <label htmlFor="country" className="form-label">
            Title
          </label>
          <input
            id="country"
            className="form-control"
            value={this.state.country}
            onChange={(event) => {
              this.handleCountryChange(event);
            }}
          />
        </div>
        <div className="my-3">
          <label htmlFor="title" className="form-label">
            Title
          </label>
          <input
            id="title"
            className="form-control"
            value={this.state.title}
            onChange={(event) => {
              this.handleTitleChange(event);
            }}
          />
        </div>
        <div className="my-3">
          <label htmlFor="body" className="form-label">
            Body
          </label>
          <textarea
            id="body"
            className="form-control"
            value={this.state.body}
            onChange={(event) => {
              this.handleBodyChange(event);
            }}
          ></textarea>
        </div>
        <button type="submit" className="btn btn-primary">
          Create a new log
        </button>
      </form>
    );
  }
}
