import React from "react";
import "./style.css";
import "bootstrap/dist/css/bootstrap.css";
import { Link } from "react-router-dom";
export default class ContinentLogs extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      data: [],
      continentId: "",
    };
  }

  componentDidMount() {
    const cid = this.props.match.params.continentId;
    // console.log(cid);
    fetch("http://localhost:4000/api/logs")
      .then((response) => {
        return response.json();
      })
      .then((json) => {
        // console.log(json);
        // console.log(json.map((each) => each.continentId === cid));
        for (let i = 0; i < json.length; i++) {
          if (json[i].continentId === cid) {
            this.setState({
              data: [...this.state.data, json[i]],
            });
          } else {
            // console.log("no");
          }
        }
        this.setState({
          continentId: cid,
        });
      });
  }
  render() {
    return (
      <div className="container-fluid">
        <thead>
          <tr className="row">
            <th className="col">Continent</th>
            <th className="col">Country</th>
            <th className="col">Trip Year</th>
            <th className="col">Log</th>
            <th className="col"></th>
          </tr>
        </thead>
        <tbody>
          {this.state.data.map((each) => (
            <tr key={each.id} className="row">
              <td className="col">{each.continentId}</td>
              <td className="col">{each.country}</td>
              <td className="col">{each.year}</td>
              <td className="col">
                {" "}
                <Link to={`/logs/${each.id}`}>{each.title}</Link>
                <td className="col"></td>
              </td>
            </tr>
          ))}
        </tbody>
      </div>
    );
  }
}
