import React from "react";
import { toast } from "react-toastify";
import "./style.css";
import "bootstrap/dist/css/bootstrap.css";
import { Link } from "react-router-dom";
export default class Logs extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      data: [],
    };
  }
  deletePost(id) {
    const isConfirmed = window.confirm("Are you sure to delete this log?");
    if (!isConfirmed) {
      return;
    }
    fetch(`http://localhost:4000/api/logs${id}`, {
      method: "DELETE",
    }).then(() => {
      toast.success(`The log "${this.state.log.title}" was deleted`);
      this.props.history.push("/");
    });
  }
  componentDidMount() {
    fetch("http://localhost:4000/api/logs")
      .then((response) => {
        return response.json();
      })
      .then((json) => {
        this.setState({
          data: json,
        });
      });
  }
  render() {
    return (
      <div className="container-fluid">
        <thead>
          <tr className="row">
            <th className="col">Continent</th>
            <th className="col">Country</th>
            <th className="col">Trip Year</th>
            <th className="col">Log</th>
            <th className="col"></th>
          </tr>
        </thead>
        <tbody>
          {this.state.data.map((each) => (
            <tr key={each.id} className="row">
              <td className="col">{each.continentId}</td>
              <td className="col">{each.country}</td>
              <td className="col">{each.year}</td>
              <td className="col">
                {" "}
                <Link to={`/logs/${each.id}`}>{each.title}</Link>
                <td className="col">
                  <button
                    type="button"
                    className="btn btn-danger"
                    onClick={() => {
                      this.deletePost(each.id);
                    }}
                  >
                    Delete
                  </button>
                </td>
              </td>
            </tr>
          ))}
        </tbody>
      </div>
    );
  }
}
