import React from "react";
import { toast } from "react-toastify";
export default class EditPostForm extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      title: "",
      year: "",
      body: "",
    };
  }
  handleYearChange(event) {
    this.setState({
      year: event.target.value,
    });
  }
  handleTitleChange(event) {
    this.setState({
      title: event.target.value,
    });
  }
  handleBodyChange(event) {
    this.setState({
      body: event.target.value,
    });
  }
  handleSubmit(event) {
    event.preventDefault();
    const id = this.props.match.params.logId;
    // const id = this.state.id;
    fetch(`http://localhost:4000/api/logs/${id}`, {
      method: "PUT", // edit
      body: JSON.stringify({
        title: this.state.title,
        year: this.state.year,
        body: this.state.body,
      }),
      headers: {
        "Content-type": "application/json",
      },
    })
      .then((response) => {
        return response.json();
      })
      .then((json) => {
        console.log(json);
        toast.success(`Post: "${json.title}" was successfully updated`);
        this.props.history.push("/");
      });
  }
  componentDidMount() {
    const id = this.props.match.params.postId;
    fetch(`http://localhost:4000/api/logs/${id}`)
      .then((response) => {
        return response.json();
      })
      .then((json) => {
        console.log(json);
        this.setState(json);
      });
  }

  render() {
    return (
      <form
        onSubmit={(event) => {
          this.handleSubmit(event);
        }}
      >
        <div className="my-3">
          <label htmlFor="title" className="form-label">
            Title
          </label>
          <input
            id="title"
            className="form-control"
            value={this.state.title}
            onChange={(event) => {
              this.handleTitleChange(event);
            }}
          />
        </div>
        <div className="my-3">
          <label htmlFor="body" className="form-label">
            Body
          </label>
          <textarea
            id="body"
            className="form-control"
            value={this.state.body}
            onChange={(event) => {
              // this = component instance
              this.handleBodyChange(event);
            }}
          ></textarea>
        </div>
        <button type="submit" className="btn btn-primary">
          Edit Post
        </button>
      </form>
    );
  }
}
