import React from "react";
import "./style.css";
import "bootstrap/dist/css/bootstrap.css";
import Modal from "./Modal";
import { toast } from "react-toastify";
function validate_form(name, comment) {
  let errors = {
    error_name: "",
    error_comment: "",
  };
  if (name === "") {
    errors.error_name = "Commenter's name cannot be empty";
  }
  if (comment === "") {
    errors.error_comment = "Comment cannot be empty";
  }
  return errors;
}
export default class Comment extends React.Component {
  constructor(props) {
    super(props);

    this.state = {
      isModalOpen: false,
      name: "",
      comment: "",
      time: "",
      errors: {
        error_name: "",
        error_comment: "",
      },
    };
  }
  handleCommentChange = (event) => {
    this.setState({
      comment: event.target.value,
    });
  };
  handleNameChange = (event) => {
    this.setState({
      name: event.target.value,
    });
  };
  handleSubmit = (event) => {
    event.preventDefault();
    const name = this.state.name;
    const comment = this.state.comment;
    const errors = validate_form(name, comment);
    if (errors.error_comment !== "" || errors.error_name !== "") {
      this.setState({
        errors: {
          error_name: errors.error_name,
          error_comment: errors.error_comment,
        },
      });
      return;
    } else {
      this.setState({
        isModalOpen: false,
      });
      fetch("http://localhost:4000/api/comments", {
        method: "POST",
        body: JSON.stringify({
          name: this.state.name,
          comment: this.state.comment,
          time: new Date().getTime(),
          error_name: "",
          error_comment: "",
        }),
        headers: {
          "Content-type": "application/json",
        },
      })
        .then((response) => {
          return response.json();
        })
        .then((json) => {
          console.log(json);
          toast.success(`Your comment was successfully sent`);
          this.props.history.push("/comment");
        });
    }
  };
  render() {
    return (
      <div className="App">
        <button
          className="btn btn-primary"
          type="button"
          onClick={() => {
            this.setState({ isModalOpen: true });
          }}
        >
          Comment
        </button>

        {this.state.isModalOpen && (
          <Modal
            title="Add a comment"
            state={this.state}
            onClose={() => {
              this.setState({
                isModalOpen: false,
                name: "",
                comment: "",
                time: "",
                errors: {
                  error_name: "",
                  error_comment: "",
                },
              });
            }}
            handleSubmit={this.handleSubmit}
            handleNameChange={this.handleNameChange}
            handleCommentChange={this.handleCommentChange}
          />
        )}
        <div className="container-fluid">
          <thead>
            <tr className="row">
              <th className="col">Continent</th>
              <th className="col">Country</th>
              <th className="col">Trip Year</th>
              <th className="col">Log</th>
              <th className="col"></th>
            </tr>
          </thead>
          <tbody>
            {/* {this.state.data.map((each) => (
            <tr key={each.id} className="row">
              <td className="col">{each.continentId}</td>
              <td className="col">{each.country}</td>
              <td className="col">{each.year}</td>
              <td className="col">
                {" "}
                <Link to={`/logs/${each.id}`}>{each.title}</Link>
                <td className="col"></td>
              </td>
            </tr>
          ))} */}
          </tbody>
        </div>
      </div>
    );
  }
}
